
var storage = require("./src/node-persist.js");

storage.init({continuous: false})
    .then(function() {
        console.log("loaded");
        console.log(storage.getItem('batman'));


        storage.setItem('batman', "yo dawg").then(
            function() {
                console.log("setItem.success");
            },
            function() {
                console.log("setItem.error");
            }
        );

        setTimeout(function() {
            console.log("3 seconds later");
            storage.persist();
        }, 3000);
    });



